import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Accordion Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Collapsed', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-accordion--playground&viewMode=story');
    await expect(page.locator('.panel')).toHaveScreenshot();
  });
  test('Opening', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-accordion--playground&viewMode=story');
    await page.locator('hmc-accordion').click();
    await expect(page.locator('.panel')).toHaveScreenshot();
  });
  test('Opened (Small Header)', async ({ page }) => {
    await page.goto('/iframe.html?args=isOpen:true;size:xs&id=components-accordion--playground&viewMode=story');
    await expect(page.locator('.panel')).toHaveScreenshot();
  });
});
