import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Badge Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Urgent', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-badge--urgent&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
  test('Information', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-badge--information&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
});
