import { newSpecPage } from '@stencil/core/testing';
import { HmcBadge } from '../badge';
describe('hmc-badge', () => {
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcBadge],
      html: `<hmc-badge style="urgent" type="digits" number="1"></hmc-badge>`,
    });
    expect(page.root).toMatchInlineSnapshot(`
      <hmc-badge number="1" type="digits">
        <mock:shadow-root>
          <div class="badge digits urgent">
            <span class="label urgent">
              1
            </span>
          </div>
        </mock:shadow-root>
      </hmc-badge>
    `);
  });
});
