import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Banner Message Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Low Density', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-banner-message--statuses&viewMode=story');
    await expect(page.locator('.banners')).toHaveScreenshot();
  });
  test('High Density', async ({ page }) => {
    await page.goto('/iframe.html?args=density:high&id=components-banner-message--statuses&viewMode=story');
    await expect(page.locator('.banners')).toHaveScreenshot();
  });
  test('No Close Button or Icon', async ({ page }) => {
    await page.goto('/iframe.html?args=isClosable:false&id=components-banner-message--playground&viewMode=story');
    await expect(page.locator('hmc-banner-message')).toHaveScreenshot();
  });
});
