import { newSpecPage } from '@stencil/core/testing';
import { HmcBannerMessage } from '../banner-message';
describe('hmc-banner-message', () => {
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcBannerMessage],
      html: `<hmc-banner-message></hmc-banner-message>`,
    });
    expect(page.root).toMatchInlineSnapshot(`
      <hmc-banner-message>
        <mock:shadow-root>
          <div class="banner info">
            <div class="content-container">
              <hmc-icon name="info-m"></hmc-icon>
              <div class="main-content">
                <slot></slot>
              </div>
            </div>
            <div class="action-container"></div>
          </div>
        </mock:shadow-root>
      </hmc-banner-message>
    `);
  });
});
