import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Button Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Primary', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-button--primary&viewMode=story');
    await expect(page.locator('.buttons')).toHaveScreenshot();
  });
  test('Secondary', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-button--secondary&viewMode=story');
    await expect(page.locator('.buttons')).toHaveScreenshot();
  });
  test('Tertiary', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-button--tertiary&viewMode=story');
    await expect(page.locator('.buttons')).toHaveScreenshot();
  });
  test('Ghost', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-button--ghost&viewMode=story');
    await expect(page.locator('.buttons')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-button--sizes&viewMode=story');
    await expect(page.locator('.buttons')).toHaveScreenshot();
  });
  test('Full-Width', async ({ page }) => {
    await page.goto('/iframe.html?args=isFullWidth:true&id=components-button--playground&viewMode=story');
    await expect(page.locator('hmc-button')).toHaveScreenshot();
  });
});
