import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Card Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Densities', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-card--densities&viewMode=story');
    await expect(page.locator('.card-container')).toHaveScreenshot();
  });
  test('Header and Footer Slots', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-card--variants&viewMode=story');
    await expect(page.locator('.card-container')).toHaveScreenshot();
  });
});
