import { newSpecPage } from '@stencil/core/testing';
import { HmcCard } from '../card';
describe('hmc-card', () => {
  let page;
  beforeEach(async () => {
    page = await newSpecPage({
      components: [HmcCard],
    });
  });
  it('builds', async () => {
    expect(new HmcCard()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      await page.setContent('<hmc-card></hmc-card>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-card>
          <mock:shadow-root>
            <div class="card">
              <div class="card-content">
                <slot></slot>
              </div>
            </div>
          </mock:shadow-root>
        </hmc-card>
      `);
    });
  });
});
