import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Checkbox Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('States', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-checkbox--states&viewMode=story');
    await expect(page.locator('.grid')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-checkbox--sizes&viewMode=story');
    await expect(page.locator('#root-inner')).toHaveScreenshot();
  });
});
