import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Chip Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Selection', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-chip--selection&viewMode=story');
    await expect(page.locator('.flex-display')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-chip--sizes&viewMode=story');
    await expect(page.locator('.flex-display')).toHaveScreenshot();
  });
});
