import { newSpecPage } from '@stencil/core/testing';
import { HmcContainerHeader } from '../container-header';
describe('hmc-container-header', () => {
  it('builds', async () => {
    expect(new HmcContainerHeader()).toBeTruthy();
  });
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcContainerHeader],
      html: `<hmc-container-header></hmc-container-header>`,
    });
    expect(page.root).toEqualHtml(`
    <hmc-container-header>
       <mock:shadow-root>
         <div class="container-header">
           <div class="icon-wrapper">
             <slot name="icon"></slot>
           </div>
           <div class="title-group">
             <div class="heading"></div>
             <div class="subtitle"></div>
           </div>
         </div>
       </mock:shadow-root>
     </hmc-container-header>    `);
  });
  it('renders with subtitle', async () => {
    const page = await newSpecPage({
      components: [HmcContainerHeader],
      html: `<hmc-container-header heading="Test" subtitle="World"></hmc-container-header>`,
    });
    expect(page.root).toEqualHtml(`
    <hmc-container-header heading="Test" subtitle="World">
       <mock:shadow-root>
         <div class="container-header">
           <div class="icon-wrapper">
             <slot name="icon"></slot>
           </div>
           <div class="title-group">
             <div class="heading">
             Test
             </div>
             <div class="subtitle">
              World
             </div>
           </div>
         </div>
       </mock:shadow-root>
     </hmc-container-header>
    `);
  });
});
