import { newSpecPage } from '@stencil/core/testing';
import { HmcDropdownGroup } from '../dropdown-group';
describe('hmc-dropdown-group', () => {
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcDropdownGroup],
      html: `<hmc-dropdown-group group-title="Group Title" size="md"></hmc-dropdown-group>`,
    });
    expect(page.root).toMatchInlineSnapshot(`
      <hmc-dropdown-group group-title="Group Title" size="md">
        <mock:shadow-root>
          <div class="group md">
            <div class="title">
              Group Title
            </div>
            <slot></slot>
          </div>
        </mock:shadow-root>
      </hmc-dropdown-group>
    `);
  });
});
