import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Dropdown Option Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Variants', async ({ page }) => {
    await page.goto('/iframe.html?args=placeholder-m&id=components-dropdown-option--states&viewMode=story');
    await expect(page.locator('.list-container')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=placeholder-m&id=components-dropdown-option--sizes&viewMode=story');
    await expect(page.locator('.list-container')).toHaveScreenshot();
  });
});
