import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Dropdown Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('States', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-dropdown--states&viewMode=story');
    await expect(page.locator('.flex-display')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-dropdown--sizes&viewMode=story');
    await expect(page.locator('.flex-display')).toHaveScreenshot();
  });
  test('Multiple-Choice', async ({ page }) => {
    await page.goto('/iframe.html?args=selectionType:Double&id=components-dropdown--playground&viewMode=story');
    await expect(page.locator('#root-inner')).toHaveScreenshot();
  });
});
