import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Icon Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Coloured', async ({ page }) => {
    await page.goto('/iframe.html?args=color:!hex(00ff00);placeholder-m&id=components-icon--playground&viewMode=story');
    await expect(page.locator('hmc-icon')).toHaveScreenshot();
  });
  test('All Icons', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-icon--supported-icons&viewMode=story');
    await expect(page.locator('.list')).toHaveScreenshot();
  });
});
