import { newSpecPage } from "@stencil/core/testing";
import { HmcIcon } from '../icon';
describe('hmc-icon', () => {
  it('builds', async () => {
    expect(new HmcIcon()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      const { root } = await newSpecPage({
        components: [HmcIcon],
        html: '<hmc-icon name="add"></hmc-icon>',
      });
      expect(root).toEqualHtml(`
        <hmc-icon name="add">
          <mock:shadow-root>
            <div class="icon-wrapper">
            </div>
          </mock:shadow-root>
        </hmc-icon>
      `);
    });
  });
});
