import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Inline Message Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Statuses', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-inline-message--statuses&viewMode=story');
    await expect(page.locator('#root-inner')).toHaveScreenshot();
  });
});
