import { newSpecPage } from '@stencil/core/testing';
import { HmcMessage } from '../message';
describe('hmc-message', () => {
  let page;
  beforeEach(async () => {
    page = await newSpecPage({
      components: [HmcMessage],
    });
  });
  it('builds', () => {
    expect(new HmcMessage()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      await page.setContent('<hmc-message><p>Test</p></hmc-message>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-message>
          <mock:shadow-root>
            <div class="info message">
              <hmc-icon name="info-s"></hmc-icon>
              <slot></slot>
            </div>
          </mock:shadow-root>
          <p>
            Test
          </p>
        </hmc-message>
      `);
    });
    it('should render component with status info and custom icon', async () => {
      await page.setContent('<hmc-message status="error" icon-name="add-m"><a>Test</a></hmc-message>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-message icon-name="add-m" status="error">
          <mock:shadow-root>
            <div class="error message">
              <hmc-icon name="add-m"></hmc-icon>
              <slot></slot>
            </div>
          </mock:shadow-root>
          <a>
            Test
          </a>
        </hmc-message>
      `);
    });
  });
});
