import { test, expect } from '@playwright/test';
import { testUtils } from '../../../../utils/test.util';
test.describe('Modal Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Low Density', async ({ page }) => {
    await page.goto('/iframe.html?args=hasHeaderIcon:true&id=components-modal--modal&viewMode=story');
    await expect(page.locator('.overlay')).toHaveScreenshot();
  });
  test('Medium Density', async ({ page }) => {
    await page.goto('/iframe.html?args=hasHeaderIcon:true;density:medium&id=components-modal--modal&viewMode=story');
    await expect(page.locator('.overlay')).toHaveScreenshot();
  });
  test('Without Header', async ({ page }) => {
    await page.goto('/iframe.html?args=hasHeader:false&id=components-modal--modal&viewMode=story');
    await expect(page.locator('.overlay')).toHaveScreenshot();
  });
  test('Without Footer', async ({ page }) => {
    await page.goto('/iframe.html?args=hasFooter:false&id=components-modal--modal&viewMode=story');
    await expect(page.locator('.overlay')).toHaveScreenshot();
  });
});
