import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Nav Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Nav Links', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-nav--nav-links&viewMode=story');
    await expect(page.locator('#root')).toHaveScreenshot();
  });
  test('Nav Bar', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-nav--nav-bar&viewMode=story');
    await expect(page.locator('#root')).toHaveScreenshot();
  });
});
