import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Radio Button Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Selected', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-radio--playground&viewMode=story');
    await page.locator('hmc-radio-button', { hasNotText: 'Label' }).click();
    await expect(page.locator('.masters')).toHaveScreenshot();
  });
  test('Small Labels', async ({ page }) => {
    await page.goto('/iframe.html?args=size:xs&id=components-radio--playground&viewMode=story');
    await expect(page.locator('.masters')).toHaveScreenshot();
  });
  test('States', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-radio--states&viewMode=story');
    await expect(page.locator('.radios')).toHaveScreenshot();
  });
  test('with Hint', async ({ page }) => {
    await page.goto('/iframe.html?args=hasHint:true&id=components-radio--playground&viewMode=story');
    await expect(page.locator('.masters')).toHaveScreenshot();
  });
});
