import { newSpecPage } from '@stencil/core/testing';
import { HmcRadioButton } from '../radio-button/radio-button';
describe('hmc-radio-button', () => {
  let page;
  beforeEach(async () => {
    page = await newSpecPage({
      components: [HmcRadioButton],
    });
  });
  it('builds', async () => {
    expect(new HmcRadioButton()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      await page.setContent('<hmc-radio-button></hmc-radio-button>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-radio-button role="radio" tabindex="-1">
          <mock:shadow-root>
            <div class="container">
              <div class="radio-button">
                <div class="radio-inner"></div>
              </div>
              <label class="label md" htmlfor="hmc-rb-1">
                <span class="hint">
                  <slot name="hint"></slot>
                </span>
              </label>
            </div>
            <input id="hmc-rb-1" tabindex="-1" type="radio">
          </mock:shadow-root>
        </hmc-radio-button>
      `);
    });
  });
});
