import { newSpecPage } from '@stencil/core/testing';
import { HmcRadioGroup } from '../radio-group/radio-group';
describe('hmc-radio-group', () => {
  let page;
  beforeEach(async () => {
    page = await newSpecPage({
      components: [HmcRadioGroup],
    });
  });
  it('builds', async () => {
    expect(new HmcRadioGroup()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      await page.setContent('<hmc-radio-group></hmc-radio-group>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-radio-group role="radiogroup">
          <mock:shadow-root>
            <slot></slot>
          </mock:shadow-root>
        </hmc-radio-group>
      `);
    });
  });
});
