import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Segmented Control Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Full-Width Small', async ({ page }) => {
    await page.goto('/iframe.html?args=size:sm;isFullWidth:true&id=components-segmented-control--playground&viewMode=story');
    await expect(page.locator('hmc-segmented-control')).toHaveScreenshot();
  });
  test('States', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-segmented-control--states&viewMode=story');
    await expect(page.locator('.segment-button-grid')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-segmented-control--sizes&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
  test('Icons', async ({ page }) => {
    await page.goto('/iframe.html?args=isIconOnly:true&id=components-segmented-control--sizes&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
});
