import { newSpecPage } from '@stencil/core/testing';
import { HmcSegmentedButton } from '../segmented-button/segmented-button';
import { HmcSegmentedControl } from '../segmented-control/segmented-control';
describe('hmc-segmented-control', () => {
  it('builds', async () => {
    expect(new HmcSegmentedControl()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      const { root } = await newSpecPage({
        components: [HmcSegmentedControl, HmcSegmentedButton],
        html: `<hmc-segmented-control value="0">
                 <hmc-segmented-button value="0"/>
               </hmc-segmented-control>`,
      });
      expect(root).toMatchInlineSnapshot(`
        <hmc-segmented-control role="group" value="0">
          <mock:shadow-root>
            <slot></slot>
          </mock:shadow-root>
          <hmc-segmented-button pressed="" value="0">
            <mock:shadow-root>
              <button aria-pressed="true" class="first-child last-child md pressed segmented-button" tabindex="0">
                <slot></slot>
              </button>
            </mock:shadow-root>
          </hmc-segmented-button>
        </hmc-segmented-control>
      `);
    });
  });
});
