import { newSpecPage } from '@stencil/core/testing';
import { HmcSkeleton } from '../skeleton';
describe('hmc-skeleton', () => {
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcSkeleton],
      html: `<hmc-skeleton animation-enable="false" animation-position="start"></hmc-badge>`,
    });
    expect(page.root).toMatchInlineSnapshot(`
      <hmc-skeleton animation-enable="false" animation-position="start">
        <mock:shadow-root>
          <div class="skeleton">
            <div class="box">
              <div class="line"></div>
            </div>
          </div>
        </mock:shadow-root>
      </hmc-skeleton>
    `);
  });
});
