import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Spinner Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Kinds', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-spinner--kinds&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-spinner--sizes&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
});
