import { newSpecPage } from '@stencil/core/testing';
import { HmcSpinner } from '../spinner';
describe('hmc-spinner', () => {
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcSpinner],
      html: `<hmc-spinner kind="primary"></hmc-spinner>`,
    });
    expect(page.root).toMatchInlineSnapshot(`
      <hmc-spinner kind="primary">
        <mock:shadow-root>
          <div class="primary sm spinner" role="status">
            <span class="visually-hidden">
              <slot></slot>
            </span>
          </div>
        </mock:shadow-root>
      </hmc-spinner>
    `);
  });
});
