import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Tab-Group Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('New Tab', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-tabs--playground&viewMode=story');
    await page.locator('hmc-tab', { hasText: ' Tab 3 Label ' }).click();
    await expect(page.locator('hmc-tab-group')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=isSelected:true&id=components-tabs--sizes&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
  test('Full-Width', async ({ page }) => {
    await page.goto('/iframe.html?args=isFullWidth:true&id=components-tabs--playground&viewMode=story');
    await expect(page.locator('hmc-tab-group')).toHaveScreenshot();
  });
});
