import { newSpecPage } from '@stencil/core/testing';
import { HmcTabGroup } from '../tab-group/tab-group';
import { HmcTab } from '../tab/tab';
describe('hmc-tab-group', () => {
  let page;
  beforeEach(async () => {
    page = await newSpecPage({
      components: [HmcTabGroup, HmcTab],
    });
  });
  it('builds', async () => {
    expect(new HmcTabGroup()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      await page.setContent('<hmc-tab-group></hmc-tab-group>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-tab-group>
          <mock:shadow-root>
            <div class="tabs-container" role="tablist">
              <slot></slot>
            </div>
            <div class="tab-panels">
              <slot name="panel"></slot>
            </div>
          </mock:shadow-root>
        </hmc-tab-group>
      `);
    });
  });
});
