import { newSpecPage } from '@stencil/core/testing';
import { HmcTab } from '../tab/tab';
describe('hmc-tab', () => {
  it('builds', async () => {
    expect(new HmcTab()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      const { root } = await newSpecPage({
        components: [HmcTab],
        html: '<hmc-tab></hmc-tab>',
      });
      expect(root).toMatchInlineSnapshot(`
        <hmc-tab aria-disabled="false" aria-selected="false" role="tab" tabindex="-1">
          <mock:shadow-root>
            <div class="md title-container">
              <slot></slot>
            </div>
          </mock:shadow-root>
        </hmc-tab>
      `);
    });
  });
});
