import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Tag Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Subtle Tags', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-tag--statuses&viewMode=story');
    await expect(page.locator('.tags')).toHaveScreenshot();
  });
  test('Strong Tags', async ({ page }) => {
    await page.goto('/iframe.html?args=emphasis:strong&id=components-tag--statuses&viewMode=story');
    await expect(page.locator('.tags')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-tag--sizes&viewMode=story');
    await expect(page.locator('.tags')).toHaveScreenshot();
  });
  test('Full-Width', async ({ page }) => {
    await page.goto('/iframe.html?args=isFullWidth:true;emphasis:strong&id=components-tag--playground&viewMode=story');
    await expect(page.locator('hmc-tag')).toHaveScreenshot();
  });
});
