import { newSpecPage } from '@stencil/core/testing';
import { HmcTag } from '../tag';
describe('hmc-tag', () => {
  let page;
  beforeEach(async () => {
    page = await newSpecPage({
      components: [HmcTag],
    });
  });
  it('builds', () => {
    expect(new HmcTag()).toBeTruthy();
  });
  describe('renders', () => {
    it('should render default component', async () => {
      await page.setContent('<hmc-tag><p>Test</p></hmc-tag>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-tag>
          <mock:shadow-root>
            <div class="info md subtle tag">
              <hmc-icon name="info-s"></hmc-icon>
              <slot></slot>
            </div>
          </mock:shadow-root>
          <p>
            Test
          </p>
        </hmc-tag>
      `);
    });
    it('should render component with status error and custom icon', async () => {
      await page.setContent('<hmc-tag status="error" icon-name="add-m"><a>Test</a></hmc-tag>');
      expect(page.root).toMatchInlineSnapshot(`
        <hmc-tag icon-name="add-m" status="error">
          <mock:shadow-root>
            <div class="error md subtle tag">
              <hmc-icon name="add-m"></hmc-icon>
              <slot></slot>
            </div>
          </mock:shadow-root>
          <a>
            Test
          </a>
        </hmc-tag>
      `);
    });
  });
});
