import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Textarea Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Filled with Counter', async ({ page }) => {
    await page.goto('/iframe.html?args=isClearable:true;isRequired:false&id=components-textarea--playground&viewMode=story');
    await page.getByLabel('Label').fill('Filled TextArea');
    await expect(page.locator('.textarea-group')).toHaveScreenshot();
  });
  test('Full-Width with Resize handle', async ({ page }) => {
    await page.goto('/iframe.html?args=resize:vertical;isFullWidth:true&id=components-textarea--playground&viewMode=story');
    await expect(page.locator('.textarea-group')).toHaveScreenshot();
  });
  test('States', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-textarea--states&viewMode=story');
    await expect(page.locator('.container')).toHaveScreenshot();
  });
  test('Sizes', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-textarea--sizes&viewMode=story');
    await expect(page.locator('.input-table-flex')).toHaveScreenshot();
  });
});
