import { newSpecPage } from '@stencil/core/testing';
import { HmcToastMessage } from '../toast-message';
describe('hmc-toast-message', () => {
  it('renders', async () => {
    const page = await newSpecPage({
      components: [HmcToastMessage],
      html: `<hmc-toast-message></hmc-toast-message>`,
    });
    expect(page.root).toMatchInlineSnapshot(`
      <hmc-toast-message>
        <mock:shadow-root>
          <div class="info toast" role="status">
            <div class="content-container">
              <hmc-icon name="info-m"></hmc-icon>
              <div class="main-content">
                <slot></slot>
              </div>
            </div>
            <div class="action-container"></div>
          </div>
        </mock:shadow-root>
      </hmc-toast-message>
    `);
  });
});
