import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Toggle Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('States', async ({ page }) => {
    await page.goto('/iframe.html?args=&id=components-toggle--states&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
  test('Small States', async ({ page }) => {
    await page.goto('/iframe.html?args=size:xs&id=components-toggle--states&viewMode=story');
    await expect(page.locator('.flex')).toHaveScreenshot();
  });
  test('Full-Width', async ({ page }) => {
    await page.goto('/iframe.html?args=isFullWidth:true&id=components-toggle--playground&viewMode=story');
    await expect(page.locator('hmc-toggle')).toHaveScreenshot();
  });
});
