import { test, expect } from '@playwright/test';
import { testUtils } from '../../../utils/test.util';
test.describe('Tooltip Snapshot Test', () => {
  test.beforeEach(async ({ page }) => {
    await testUtils({ page });
  });
  test('Tooltip', async ({ page }) => {
    await page.goto('/iframe.html?args=showTooltip:true;text:A+Tooltip&id=components-tooltip--tooltip&viewMode=story');
    await page.locator('hmc-input-field').hover();
    await expect(page.locator('.container')).toHaveScreenshot();
  });
  test('Tooltip - Fixed-Width', async ({ page }) => {
    await page.goto('/iframe.html?args=isFixedWidth:true;text:A+Tooltip&id=components-tooltip--tooltip&viewMode=story');
    await page.locator('hmc-input-field').hover();
    await expect(page.locator('.container')).toHaveScreenshot();
  });
  test('Toggletip', async ({ page }) => {
    await page.goto('/iframe.html?args=placement:right&id=components-tooltip--toggletip&viewMode=story');
    await page.locator('hmc-button').click();
    await expect(page.locator('.toggle-container')).toHaveScreenshot();
  });
  test('Toggletip with Action Button', async ({ page }) => {
    await page.goto('/iframe.html?args=placement:right;action:true&id=components-tooltip--toggletip&viewMode=story');
    await page.locator('hmc-button', { has: page.locator('.ghost') }).click();
    await expect(page.locator('.toggle-container')).toHaveScreenshot();
  });
  test('Placements', async ({ page }) => {
    await page.goto('/iframe.html?args=isOpen:true&id=components-tooltip--placement&viewMode=story');
    await expect(page.locator('body')).toHaveScreenshot();
  });
});
