export type MessageStatusType = 'success' | 'warning' | 'error' | 'info';
export type InputStatusType = 'normal' | Exclude<MessageStatusType, 'success'>;
export type DropdownStatusType = Exclude<InputStatusType, 'info'>;
export type TagStatusType = MessageStatusType | 'highlight';
export type BadgeStatusType = 'urgent' | 'information';
export type ProgressBarStatusType = 'info' | 'success' | 'error';
